package com.example.lesson18.presentation

import android.os.Bundle
import android.view.View
import android.widget.Toast
import androidx.fragment.app.Fragment
import com.example.lesson18.CoroutinesTest
import com.example.lesson18.R
import com.example.lesson18.utils.loadImageFromNetworkWithGlide
import com.example.lesson18.utils.navigateToFragment
import kotlinx.android.synthetic.main.fragment_main.*
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers.IO
import kotlinx.coroutines.Dispatchers.Main
import kotlinx.coroutines.async
import kotlinx.coroutines.launch
import kotlinx.coroutines.withContext

class MainFragment : Fragment(R.layout.fragment_main) {

    val animals = listOf<String>(
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,"
    )

    val anime = listOf<String>(
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,"
    )

    val animunculus = listOf<String>(
        "data:image/jpeg;base64,",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQF0Yj2x4Q2Y1NWEi3z-hcq0Zx7GuH7RZTvfA&usqp=CAU",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsbUuc5f7ftKUbA4wig1R-kTV-R_SSH5pGow&usqp=CAU"
    )

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {

        CoroutineScope(IO).launch {
            val result = testCoroutines()
            withContext(Main) {
                Toast.makeText(requireContext(),
                    "First coroutine async test: ${result.toString()}", Toast.LENGTH_SHORT)
                    .show()
            }
        }

        CoroutineScope(IO).launch {
            val resultTest = CoroutinesTest().getString()
            withContext(Main) {
                Toast.makeText(requireContext(),
                    "Second coroutine async test: $resultTest", Toast.LENGTH_SHORT)
                    .show()
            }
        }

        btnFindPicture.setOnClickListener() {
            val currentFragment = this
            CoroutineScope(IO).launch {
                val fragment = currentFragment

                val query = etFindPicture.text.toString()
                val currentList: List<String>

                when (query) {
                    "animal" -> currentList = animals
                    "animals" -> currentList = animals
                    "animuncul" -> currentList = animunculus
                    "animunculus" -> currentList = animunculus
                    "anime" -> currentList = anime
                    "" -> {
                        withContext(Main) {
                            currentList = listOf<String>()
                            Toast.makeText(requireContext(), "Empty query!", Toast.LENGTH_SHORT)
                                .show()
                        }
                    }
                    else -> {
                        currentList = listOf<String>()
                        withContext(Main) {
                            val bundle = Bundle()
                            bundle.putString("SEARCH_TARGET", query)

                            val webFragment = WebFragment()
                            webFragment.arguments = bundle

                            requireActivity().supportFragmentManager.navigateToFragment(webFragment)
                        }
                    }
                }

                withContext(Main) {
                    if (!currentList.isNullOrEmpty()) {
                        fragment.loadImageFromNetworkWithGlide(currentList.random(), ivPicture)
                    }
                }

            }
        }

    }

    private fun returnAddOneNumber(number: Int): Int {
        return number + 1
    }

    suspend fun testCoroutines(): Int {

        val coroutine = CoroutineScope(IO).async {
            returnAddOneNumber(number = 3)
        }

        val number = coroutine.await()

        return number
    }

}